import React, {useState} from "react";
import './style.css'
import IconSVG from './icons/icon.svg';
import {mainUrl} from '../../assets'


const Donate = () => {
    const [text, setText] = useState('');
    const [image, setImage] = useState('');

    const getInfos = async () => {
      const res = await axios.get(`${mainUrl}/nossos-parceiros`);
      const { Subtitulo, Imagens } = res.data;
      setText(Subtitulo);
      setImage(Imagens[0].url);
    };

    useEffect(() => {
      getInfos();
    }, []);

    return(
        <div className='container'>

        <img src={IconSVG} className='svg'/>
        <h1 className='title'>
            Ajude-nos!
        </h1>
        <p className='text'>
            {text}
        </p>
        <img src={image} className='img' alt='imagem de fundo'/>
        <button type="button" className='button'>Faça uma doação</button>
        <p className='pix'> PIX: 0000000000</p>

        </div>
    );
}
export default Donate;
